package com.abidingtech.www.androidbactch11.Lecture11;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.widget.ListView;

import com.abidingtech.www.androidbactch11.R;

import java.util.ArrayList;

public class CustomListActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_custom_list);
        String[] img={"https://upload.wikimedia.org/wikipedia/commons/6/63/Icon_Bird_512x512.png",
                "https://image.shutterstock.com/image-vector/woman-icon-vector-fat-design-260nw-724323535.jpg",
                "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "http://icons.iconseeker.com/png/fullsize/black-power-emoticons/anger-512x512.png"};

        ArrayList<Student> students=new ArrayList<>();
        ListView listView=findViewById(R.id.listView);

        for (int i=0;i<5;i++)
            students.add(new Student("Student "+i,img[i]));

        StudentAdapter adapter=new StudentAdapter(students,this);
        listView.setAdapter(adapter);

    }
}